const webhookData = $input.item.json.body || {};
const teamQuery = webhookData.team ? webhookData.team.toLowerCase() : null;

if (!teamQuery) {
  throw new Error('team parameter is required');
}

// NHL team abbreviations lookup
const teams = {
  'ducks': 'ana', 'anaheim': 'ana',
  'coyotes': 'ari', 'arizona': 'ari',
  'bruins': 'bos', 'boston': 'bos',
  'sabres': 'buf', 'buffalo': 'buf',
  'flames': 'cgy', 'calgary': 'cgy',
  'hurricanes': 'car', 'carolina': 'car',
  'blackhawks': 'chi', 'chicago': 'chi',
  'avalanche': 'col', 'colorado': 'col',
  'blue jackets': 'cbj', 'columbus': 'cbj',
  'stars': 'dal', 'dallas': 'dal',
  'red wings': 'det', 'detroit': 'det',
  'oilers': 'edm', 'edmonton': 'edm',
  'panthers': 'fla', 'florida': 'fla',
  'kings': 'la', 'los angeles': 'la',
  'wild': 'min', 'minnesota': 'min',
  'canadiens': 'mtl', 'montreal': 'mtl', 'habs': 'mtl',
  'predators': 'nsh', 'nashville': 'nsh',
  'devils': 'nj', 'new jersey': 'nj',
  'islanders': 'nyi', 'new york islanders': 'nyi',
  'rangers': 'nyr', 'new york rangers': 'nyr',
  'senators': 'ott', 'ottawa': 'ott',
  'flyers': 'phi', 'philadelphia': 'phi',
  'penguins': 'pit', 'pittsburgh': 'pit',
  'sharks': 'sj', 'san jose': 'sj',
  'kraken': 'sea', 'seattle': 'sea',
  'blues': 'stl', 'st louis': 'stl', 'st. louis': 'stl',
  'lightning': 'tb', 'tampa': 'tb', 'tampa bay': 'tb',
  'maple leafs': 'tor', 'leafs': 'tor', 'toronto': 'tor',
  'canucks': 'van', 'vancouver': 'van',
  'golden knights': 'vgk', 'vegas': 'vgk',
  'capitals': 'wsh', 'washington': 'wsh', 'caps': 'wsh',
  'jets': 'wpg', 'winnipeg': 'wpg'
};

let abbr = teams[teamQuery] || teamQuery;
if (abbr.length > 3) abbr = null;

if (!abbr) {
  throw new Error('Could not find team: ' + teamQuery);
}

return [{ json: { abbr: abbr, teamQuery: teamQuery } }];

const data = $input.item.json;
const teamInfo = $('Parse Team').item.json;
const events = data.events || [];
const team = data.team || {};
const teamName = team.shortDisplayName || team.displayName || teamInfo.teamQuery;

const abbrevToName = { 'ANA': 'Ducks', 'ARI': 'Coyotes', 'BOS': 'Bruins', 'BUF': 'Sabres', 'CGY': 'Flames', 'CAR': 'Hurricanes', 'CHI': 'Blackhawks', 'COL': 'Avalanche', 'CBJ': 'Blue Jackets', 'DAL': 'Stars', 'DET': 'Red Wings', 'EDM': 'Oilers', 'FLA': 'Panthers', 'LA': 'Kings', 'MIN': 'Wild', 'MTL': 'Canadiens', 'NSH': 'Predators', 'NJ': 'Devils', 'NYI': 'Islanders', 'NYR': 'Rangers', 'OTT': 'Senators', 'PHI': 'Flyers', 'PIT': 'Penguins', 'SJ': 'Sharks', 'SEA': 'Kraken', 'STL': 'Blues', 'TB': 'Lightning', 'TOR': 'Maple Leafs', 'VAN': 'Canucks', 'VGK': 'Golden Knights', 'WSH': 'Capitals', 'WPG': 'Jets' };

const now = new Date();
const upcoming = [];
for (let i = 0; i < events.length; i++) {
  const ev = events[i];
  const gameDate = new Date(ev.date);
  if (gameDate > now) {
    upcoming.push({
      opponent: ev.shortName || ev.name,
      date: ev.date,
      gameDate: gameDate
    });
  }
  if (upcoming.length >= 3) break;
}

if (upcoming.length === 0) {
  return { message: 'No upcoming games found for the ' + teamName + '.', games: [] };
}

const formatGame = function(game) {
  const d = game.gameDate;
  const today = new Date();
  const tomorrow = new Date(today);
  tomorrow.setDate(tomorrow.getDate() + 1);

  let dayStr = '';
  if (d.toDateString() === today.toDateString()) {
    dayStr = 'tonight';
  } else if (d.toDateString() === tomorrow.toDateString()) {
    dayStr = 'tomorrow';
  } else {
    dayStr = 'on ' + d.toLocaleDateString('en-US', { weekday: 'long' });
  }

  const parts = game.opponent.split(' @ ');
  let opponentAbbr = parts[0];
  let homeAway = 'vs';
  if (parts.length === 2) {
    const abbr = teamInfo.abbr.toUpperCase();
    if (parts[0] === abbr) {
      opponentAbbr = parts[1];
      homeAway = 'at the';
    } else {
      opponentAbbr = parts[0];
      homeAway = 'the';
    }
  }
  const opponent = abbrevToName[opponentAbbr] || opponentAbbr;

  return { opponent: opponent, homeAway: homeAway, dayStr: dayStr };
};

const next = formatGame(upcoming[0]);
let message = 'The ' + teamName + ' play ' + next.homeAway + ' ' + next.opponent + ' ' + next.dayStr + '.';

if (upcoming.length > 1) {
  const after = formatGame(upcoming[1]);
  message += ' After that, ' + after.homeAway + ' ' + after.opponent + ' ' + after.dayStr + '.';
}

return { message: message, team: teamName, games: upcoming };

const errorData = items[0].json;
let errorMsg = errorData.message || errorData.error || 'Unknown error';

if (errorMsg.indexOf('ECONNREFUSED') !== -1 || errorMsg.indexOf('ETIMEDOUT') !== -1) {
  errorMsg = 'Cannot connect to ESPN. Try again later.';
}

return { message: 'Error getting schedule: ' + errorMsg, error: true };
